// frontend/src/Forecast.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Forecast() {
  const [forecast, setForecast] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Change this URL to your FastAPI backend
  const API_URL = 'http://127.0.0.1:8000/forecast';

  useEffect(() => {
    axios.get(API_URL)
      .then((res) => {
        setForecast(res.data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Loading forecast...</p>;
  if (error) return <p>Error fetching forecast: {error}</p>;

  return (
    <div>
      <h2>Region: {forecast.region}</h2>
      <p>Generated at: {new Date(forecast.generated_at).toLocaleString()}</p>
      <p>SHA256: <code>{forecast.sha256}</code></p>

      <table border="1" cellPadding="5" style={{ marginTop: '20px', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>Date</th>
            <th>Predicted Cases</th>
            <th>Lower Bound</th>
            <th>Upper Bound</th>
          </tr>
        </thead>
        <tbody>
          {forecast.forecast.map((f) => (
            <tr key={f.date}>
              <td>{f.date}</td>
              <td>{f.yhat.toFixed(2)}</td>
              <td>{f.yhat_lower.toFixed(2)}</td>
              <td>{f.yhat_upper.toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Forecast;
