// ===================== App.jsx =====================
import React, { useState } from "react";
import {
  Container,
  Grid,
  Card,
  CardContent,
  Typography,
  Button,
  Drawer,
  List,
  ListItem,
  ListItemText,
  Dialog,
  Badge,
  Fab,
  Box,
  IconButton,
  Chip,
  Divider,
} from "@mui/material";

import NotificationsActiveIcon from "@mui/icons-material/NotificationsActive";
import NotificationsIcon from "@mui/icons-material/Notifications";
import CloseIcon from "@mui/icons-material/Close";
import WarningIcon from "@mui/icons-material/Warning";

import Forecast from "./components/forecast";

function App() {
  const [alertOpen, setAlertOpen] = useState(false);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [newAlert, setNewAlert] = useState(true);

  const [history, setHistory] = useState([]);

  const triggerAlert = () => {
    const alertItem = {
      time: new Date().toLocaleString(),
      severity: "High",
      message:
        "Dengue transmission risk elevated due to humidity spike + forecast trends.",
    };

    setHistory([alertItem, ...history]);
    setNewAlert(true);
    setAlertOpen(true);
  };

  // Severity color logic
  const getSeverityColor = (sev) => {
    if (sev === "High") return "#d50000";
    if (sev === "Medium") return "#ff9100";
    return "#2e7d32";
  };

  return (
    <Container maxWidth="md" sx={{ mt: 6, mb: 6 }}>
      <Grid container spacing={4} direction="column" alignItems="center">
        {/* ===================== HEADER ===================== */}
        <Grid item xs={12}>
          <Typography
            variant="h3"
            fontWeight={800}
            sx={{
              textAlign: "center",
              background:
                "linear-gradient(90deg, #ff5252, #ff1744, #d500f9, #2979ff)",
              WebkitBackgroundClip: "text",
              WebkitTextFillColor: "transparent",
            }}
          >
            EpiSure â€“ AI Epidemic Forecasting
          </Typography>

          <Typography
            variant="subtitle1"
            sx={{ textAlign: "center", mt: 1, opacity: 0.8 }}
          >
            Real-time prediction â€¢ Risk alerts â€¢ Disease intelligence
          </Typography>
        </Grid>

        {/* ===================== FORECAST CARD ===================== */}
        <Grid item xs={12} sx={{ width: "100%" }}>
          <Card
            sx={{
              borderRadius: 4,
              p: 2,
              boxShadow: "0 10px 35px rgba(0,0,0,0.15)",
              backdropFilter: "blur(10px)",
              minHeight: "400px",
            }}
          >
            <CardContent>
              <Typography variant="h5" fontWeight={700} gutterBottom>
                ðŸ“Š 7-Day Epidemiological Forecast
              </Typography>
              <Forecast />
            </CardContent>
          </Card>
        </Grid>

        {/* ===================== ALERT CARD ===================== */}
        <Grid item xs={12} sx={{ width: "100%" }}>
          <Card
            sx={{
              borderRadius: 4,
              p: 2,
              background: "rgba(255, 50, 50, 0.20)",
              backdropFilter: "blur(14px)",
              boxShadow: "0 8px 25px rgba(255,0,0,0.25)",
              minHeight: "230px",
            }}
          >
            <CardContent>
              <Typography
                variant="h5"
                fontWeight={800}
                gutterBottom
                sx={{ color: "#b71c1c" }}
              >
                ðŸ”” Real-Time Alerts
              </Typography>

              <Typography variant="body2" sx={{ opacity: 0.9 }}>
                Stay informed about risk predictions using epidemiological and environmental indicators.
              </Typography>

              <Button
                variant="contained"
                fullWidth
                startIcon={<NotificationsActiveIcon />}
                sx={{
                  mt: 3,
                  py: 1.3,
                  borderRadius: 3,
                  fontWeight: 700,
                  textTransform: "none",
                  backgroundColor: "#d50000",
                  "&:hover": { backgroundColor: "#ff1744" },
                }}
                onClick={() => triggerAlert()}
              >
                Show Dengue Alert
              </Button>

              <Button
                variant="text"
                fullWidth
                sx={{ mt: 1, color: "#b71c1c", fontWeight: 700 }}
                onClick={() => setDrawerOpen(true)}
              >
                View Alert History
              </Button>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* ===================== FLOATING BELL ===================== */}
      <Fab
        color="error"
        sx={{
          position: "fixed",
          bottom: 30,
          right: 30,
          boxShadow: "0 6px 15px rgba(0,0,0,0.3)",
        }}
        onClick={() => setDrawerOpen(true)}
      >
        <Badge
          color="warning"
          variant={newAlert ? "dot" : ""}
          overlap="circular"
          sx={{
            "& .MuiBadge-badge": {
              animation: newAlert ? "ring 1.8s infinite" : "none",
              "@keyframes ring": {
                "0%": { transform: "scale(1)" },
                "50%": { transform: "scale(1.25)" },
                "100%": { transform: "scale(1)" },
              },
            },
          }}
        >
          <NotificationsIcon />
        </Badge>
      </Fab>

      {/* ===================== ALERT HISTORY DRAWER ===================== */}
      <Drawer
        anchor="right"
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        PaperProps={{
          sx: {
            width: 350,
            background: "rgba(255,255,255,0.1)",
            backdropFilter: "blur(12px)",
            boxShadow: "0 0 25px rgba(0,0,0,0.2)",
            p: 2,
          },
        }}
      >
        <Box sx={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <Typography variant="h5" fontWeight={800}>
            Alert History
          </Typography>

          <IconButton onClick={() => setDrawerOpen(false)}>
            <CloseIcon />
          </IconButton>
        </Box>

        <Divider sx={{ mt: 1, mb: 2 }} />

        {history.length === 0 ? (
          <Typography sx={{ opacity: 0.7, mt: 2 }}>No alerts yet.</Typography>
        ) : (
          <>
            <List>
              {history.map((item, index) => (
                <ListItem
                  key={index}
                  sx={{
                    mb: 1.5,
                    borderRadius: 3,
                    background: "rgba(255,255,255,0.15)",
                    backdropFilter: "blur(8px)",
                    boxShadow: "0 0 12px rgba(0,0,0,0.15)",
                  }}
                >
                  <WarningIcon
                    sx={{
                      fontSize: 32,
                      color: getSeverityColor(item.severity),
                      mr: 1.5,
                    }}
                  />

                  <ListItemText
                    primary={
                      <Typography fontWeight={700}>{item.message}</Typography>
                    }
                    secondary={
                      <>
                        <Chip
                          label={item.severity}
                          size="small"
                          sx={{
                            backgroundColor: getSeverityColor(item.severity),
                            color: "white",
                            fontWeight: 700,
                            mr: 1,
                          }}
                        />
                        <span style={{ opacity: 0.8 }}>{item.time}</span>
                      </>
                    }
                  />
                </ListItem>
              ))}
            </List>

            <Button
              fullWidth
              sx={{
                mt: 2,
                borderRadius: 2,
                color: "white",
                backgroundColor: "#b71c1c",
                "&:hover": { backgroundColor: "#d50000" },
              }}
              onClick={() => setHistory([])}
            >
              Clear All Alerts
            </Button>
          </>
        )}
      </Drawer>
    </Container>
  );
}

export default App;
